import {Formik, useField, useFormik} from 'formik';
import * as Yup from 'yup';
import {useNavigate} from "react-router";
import {Badge, Button, Form, Row} from "react-bootstrap";

export default function Questionnaire() {
    // const navigate = useNavigate();


    const formik = useFormik({
        initialValues: {
            // q1:false,
            // q2:false,
            sq0:'',
            sq1:'',
            sq2:'',
            sq3:'',
            sq4:'',
            sq5:'',
            sq6:'',
            sq7:'',
            sq8:'',
            sq9:'',
            // firstName: '',
            // lastName: '',
            // email: '',
        },
        validationSchema: Yup.object({
            // q1: Yup.bool()
            //     .required("1Choose one")
            //     .oneOf([true], 'Field 1 must be checked'),
            //
            // q2: Yup.bool()
            //     .required("2Choose one")
            //     .oneOf([true], 'Field 2 must be checked'),

            sq0: Yup.string()
                .required("Question 0 must be checked"),

            sq1: Yup.string()
                .required("Question 1 must be checked"),

            sq2: Yup.string()
                .required("Question 2 must be checked"),

            sq3: Yup.string()
                .required("Question 3 must be checked"),

            sq4: Yup.string()
                .required("Question 4 must be checked"),

            sq5: Yup.string()
                .required("Question 5 must be checked"),
            sq6: Yup.string()
                .required("Question 6 must be checked"),
            sq7: Yup.string()
                .required("Question 7 must be checked"),
            sq8: Yup.string()
                .required("Question 8 must be checked"),
            sq9: Yup.string()
                .required("Question 9 must be checked"),

            // firstName: Yup.string()
            //     .max(15, 'Must be 15 characters or less')
            //     .required('Required'),
            // lastName: Yup.string()
            //     .max(20, 'Must be 20 characters or less')
            //     .required('Required'),
            // email: Yup.string().email('Invalid email address').required('Required'),

        }),

        onSubmit: values => {

            alert(JSON.stringify(values, null, 2));

            formik.initialValues.sq0 = '';
            formik.initialValues.sq1 = '';
            formik.initialValues.sq2 = '';
            formik.initialValues.sq3 = '';
            formik.initialValues.sq4 = '';
            formik.initialValues.sq5 = '';
            formik.initialValues.sq6 = '';
            formik.initialValues.sq7 = '';
            formik.initialValues.sq8 = '';
            formik.initialValues.sq9 = '';

                // setID1('');
                // setID2('');
                // setID3('');
            setFuns.forEach(func=>{
                func('')
            })
        },
    });


    // let [selectedID, setID] = React.useState({
    //     p1:"",
    //     p2:"",
    //     p3:"",
    //     p4:"",
    //     p5:"",
    // });

    let [selectedID0, setID0] = React.useState("");
    let [selectedID1, setID1] = React.useState("");
    let [selectedID2, setID2] = React.useState("");
    let [selectedID3, setID3] = React.useState("");
    let [selectedID4, setID4] = React.useState("");
    let [selectedID5, setID5] = React.useState("");
    let [selectedID6, setID6] = React.useState("");
    let [selectedID7, setID7] = React.useState("");
    let [selectedID8, setID8] = React.useState("");
    let [selectedID9, setID9] = React.useState("");

    let setFuns = [setID0, setID1, setID2, setID3,setID4,setID5,setID6,setID7,setID8,setID9]
    let selectedIDs = [selectedID0, selectedID1, selectedID2, selectedID3,selectedID4,selectedID5,selectedID6,selectedID7,selectedID8,selectedID9]
    function Question(props) {
        return (
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Question {props.qId+1}: {props.descri}</Form.Label>
                {!formik.values[["sq"+props.qId]] ? (
                    <Badge pill bg="danger">
                        {formik.errors[["sq"+props.qId]]}
                    </Badge>
                ) : null}
                {['Strongly Agree', 'Agree', 'netural', 'Slightly Disagree', 'Disagree', 'Strongly Disagree'].map((ques) => (
                    <div key={`${ques}+${props.qId}`} className="mb-3" >
                        <Form.Check
                            inline
                            type="radio"
                            id={`${ques}+${props.qId}`}
                            name={`${ques}+${props.qId}`}
                            // label={`${ques}+${props.qId === 1? selectedID1:selectedID2}`}
                            // label={`${ques}+${props.selectAns}`}
                            // checked={ques+props.qId === props.selectAns}
                            // checked={ques+props.qId === (props.qId === 1? selectedID1:selectedID2)}
                            label={`${ques}+${props.selectId}`}
                            checked={ques+props.qId === props.selectId}
                            onChange={ () => {
                                // formik.values["q"+props.qId] = true;
                                formik.values["sq"+props.qId]= ques;
                                setFuns[props.qId](ques+props.qId);
                                // switch (props.qId){
                                //     case 1:
                                //         console.log("case1")
                                //         setID1(ques+props.qId)
                                //         break
                                //     case 2:
                                //         console.log("case2")
                                //         setID2(ques+props.qId)
                                //         break
                                //     case 3:
                                //         console.log("case3")
                                //         setID3(ques+props.qId)
                                //         break
                                // }
                                // if (props.qId===1){
                                //     setID1(ques+props.qId)
                                //     console.log("onChange, ques+props.qId:"+ques+props.qId+", selectedID1:"+selectedID1)
                                //
                                // }else{
                                //     setID2(ques+props.qId)
                                //     console.log("onChange, ques+props.qId:"+ques+props.qId+", selectedID2:"+selectedID2)
                                // }
                                // setID(ques+props.qId)
                                // console.log("onChange, ques+props.qId:"+ques+props.qId+", selectedID:"+selectedID)
                            }}
                            // onChange={formik.handleChange}
                            // onBlur={formik.handleBlur}
                            // value={formik.values["q"+props.qId]}
                        />
                    </div>
                ))}

                {/*{formik.touched["q"+props.qId] && formik.errors[["q"+props.qId]] ? (*/}
                {/*    // <div>{formik.errors[["q"+props.qId]]}</div>*/}
                {/*{!formik.values[["sq"+props.qId]] ? (*/}
                {/*    <Badge pill bg="danger">*/}
                {/*        {formik.errors[["sq"+props.qId]]}*/}
                {/*    </Badge>*/}
                {/*) : null}*/}
            </Form.Group>
        )
    }

    const [questions, setQuestions] = useState([]);
    let firstLoad = true;
    // This method fetches the records from the database.
    useEffect(() => {
        if(!firstLoad){
            return;
        }
        firstLoad = false;
        async function getQuestions() {
            const response = await fetch(`http://localhost:5001/questionlist/`);
            if (!response.ok) {
                const message = `An error occured: ${response.statusText}`;
                window.alert(message);
                return;
            }

            const records = await response.json();
            shuffleArray(records);
            setQuestions(records.slice(0,10));
        }
        getQuestions();

        return;
    }, [questions.length]);

    function shuffleArray(array) {
        for (var i = array.length - 1; i > 0; i--) {
            var j = Math.floor(Math.random() * (i + 1));
            var temp = array[i];
            array[i] = array[j];
            array[j] = temp;
        }
    }

    function questionList() {
        return questions.map((question,index) => {
            console.log("question!!!: " + question.questiontext)
            console.log("index!!!: " + index)
            return (
                <Question
                    descri={question.questiontext}
                    key={question._id}
                    qId={index}
                    selectId = {selectedIDs[index++]}

                    // deleteRecord={() => deleteRecord(record._id)}
                    // key={record._id}
                />
            );
        });
    }
    // let
    return(
    <Form onSubmit={formik.handleSubmit}>
        {/*<Question descri={questions.questiontext}  qId={1} selectId = {selectedIDs[0]} />*/}
        {/*<Question descri={"q1 des"}  qId={1} selectId = {selectedIDs[0]}  />*/}
        {/*<Question descri={"q2 des"}  qId={2} selectId = {selectedID2} />*/}
        {/*<Question descri={"q3 des"}  qId={3} selectId = {selectedID3} />*/}
        {questionList()}

        <Button type="submit">Submit</Button>

        {/*<Form.Group className="mb-3" controlId="formBasicEmail">*/}
        {/*    <Form.Label>Question 1: Caring for people who have suffered is an important virtue.</Form.Label>*/}
        {/*    {['Strongly Agree', 'Agree','netural','Slightly Disagree','Disagree','Strongly Disagree'].map((ques) => (*/}
        {/*        <div key={`${ques}`} className="mb-3" >*/}
        {/*            <Form.Check*/}
        {/*                type="radio"*/}
        {/*                id={`${ques}`}*/}
        {/*                name={`${ques}`}*/}
        {/*                label={`${ques}`}*/}
        {/*                checked={ques === selectedID}*/}
        {/*                onChange={() => {*/}
        {/*                    formik.values.q1 = true;*/}
        {/*                    setID(ques)*/}
        {/*                }}*/}
        {/*            />*/}
        {/*        </div>*/}
        {/*    ))}*/}
        {/*    {formik.touched.q1 && formik.errors.q1 ? (*/}
        {/*        <div>{formik.errors.q1}</div>*/}
        {/*    ) : null}*/}

        {/*</Form.Group>*/}


        {/*<label htmlFor="firstName">First Name</label>*/}
        {/*<input*/}
        {/*    id="firstName"*/}
        {/*    name="firstName"*/}
        {/*    type="text"*/}
        {/*    onChange={formik.handleChange}*/}
        {/*    onBlur={formik.handleBlur}*/}
        {/*    value={formik.values.firstName}*/}
        {/*/>*/}
        {/*{formik.touched.firstName && formik.errors.firstName ? (*/}
        {/*    <div>{formik.errors.firstName}</div>*/}
        {/*) : null}*/}

        {/*<label htmlFor="lastName">Last Name</label>*/}
        {/*<input*/}
        {/*    id="lastName"*/}
        {/*    name="lastName"*/}
        {/*    type="text"*/}
        {/*    onChange={formik.handleChange}*/}
        {/*    onBlur={formik.handleBlur}*/}
        {/*    value={formik.values.lastName}*/}
        {/*/>*/}
        {/*{formik.touched.lastName && formik.errors.lastName ? (*/}
        {/*    <div>{formik.errors.lastName}</div>*/}
        {/*) : null}*/}
        {/*<label htmlFor="email">Email Address</label>*/}
        {/*<input*/}
        {/*    id="email"*/}
        {/*    name="email"*/}
        {/*    type="email"*/}
        {/*    onChange={formik.handleChange}*/}
        {/*    onBlur={formik.handleBlur}*/}
        {/*    value={formik.values.email}*/}
        {/*/>*/}
        {/*{formik.touched.email && formik.errors.email ? (*/}
        {/*    <div>{formik.errors.email}</div>*/}
        {/*) : null}*/}

        {/*<Button type="submit">Submit</Button>*/}
    </Form>
    )


}
