import {Badge, Button, Form} from "react-bootstrap";

export default function Questionnaire() {
    // const navigate = useNavigate();


    const formik = useFormik({
        initialValues: {
            q1:false,
            q2:false,
            sq1:'',
            sq2:'',
            sq3:'',
            // firstName: '',
