        //     case 3:
        //         thisId =  selectedID3
        // }
        // console.log("thisID:"+thisId)
        return (
            <Form.Group className="mb-3" controlId="formBasicEmail">
                <Form.Label>Question {props.qId}: {props.descri}</Form.Label>
                {['Strongly Agree', 'Agree', 'netural', 'Slightly Disagree', 'Disagree', 'Strongly Disagree'].map((ques) => (
                    <div key={`${ques}+${props.qId}`} className="mb-3">
                        <Form.Check
