import React, { useState } from "react";
import { useNavigate } from "react-router";
import {Badge, Button, Card, Container, Stack} from "react-bootstrap";
import {NavLink} from "react-router-dom";

export default function Home() {

    const navigate = useNavigate();
    function exportCsv(){
        // navigate("http://localhost:5001/csv")
        // console.log("in exportcsv")
        async function getCsv() {
            const response = await fetch(`http://localhost:5001/csv/`);

            if (!response.ok) {
                const message = `An error occured: ${response.statusText}`;
                window.alert(message);
                return;
            }
        }
        let password = prompt('Enter the password to download the file:');
        if(password && password.toLowerCase() == "lzhlzh"){
            // window.open("folder/history.zip")
            getCsv().then( ()=>{
                    alert("Downloaded!")
                }
            )
        }else if(password){
            alert("incorrect password!! please try again");
            exportCsv()
        }
    }

    return (
        <Stack>
            <Card className="mx-auto" style={{ width: '28rem' }}>
                <Card.Img variant="top" src=".//bg.png" />
                <Card.Body>
                    <Card.Title>
                        Welcome to <Badge pill bg="info">YourMorals.org!</Badge>
                    </Card.Title>
                    <Card.Text>
                        Welcome to YourMorals.org, where you can learn about your own morality, ethics, and/or values, moral values, ethical behaviors, personality, and political preferences
                        while also contributing to scientific research. We are a group of professors, researchers, and graduate students in social psychology.
                    </Card.Text>
                    <Button className="col-4 mx"onClick={()=>{
                        navigate("/ques")
                    }}>
                        Do Questions
                    </Button>

                    <Button className="col-4 mx-auto my-4 " variant="secondary"  onClick={exportCsv}>
                        Click to export
                    </Button>
                </Card.Body>
            </Card>

            <h1>
                Welcome to <Badge pill bg="info">YourMorals.org!</Badge>
            </h1>
            <p>
                Welcome to YourMorals.org, where you can learn about your own morality, ethics, and/or values, moral values, ethical behaviors, personality, and political preferences
                while also contributing to scientific research. We are a group of professors, researchers, and graduate students in social psychology.
            </p>
            <Button className="col-4 mx-auto"onClick={()=>{
                navigate("/ques")
            }}>
                Do Questions
            </Button>

            <Button className="col-4 mx-auto my-4 " variant="secondary"  onClick={exportCsv}>
                Click to export
            </Button>

        </Stack>

     )


}
