import React, { useState } from "react";
import { useNavigate } from "react-router";
import {Button, Container, Stack} from "react-bootstrap";
import {NavLink} from "react-router-dom";

export default function Home() {

    const navigate = useNavigate();
    function exportCsv(){
        // navigate("http://localhost:5001/csv")
        // console.log("in exportcsv")
        async function getCsv() {
            const response = await fetch(`http://localhost:5001/csv/`);

            if (!response.ok) {
                const message = `An error occured: ${response.statusText}`;
                window.alert(message);
                return;
            }
        }
        let password = prompt('Enter the password to download the file:');
        if(password && password.toLowerCase() == "lzhlzh"){
            // window.open("folder/history.zip")
            getCsv().then( ()=>{
                    alert("Downloaded!")
                }
            )
        }else if(password){
            alert("incorrect password!! please try again");
            exportCsv()
        }
    }

    return (
        <Stack>
            <Button className="col-4 mx-auto my-4 "onClick={()=>{
                navigate("/ques")
            }}>
                Do Questions
            </Button>

            <Button className="col-4 mx-auto my-4 " variant="secondary"  onClick={exportCsv}>
                Click to export
            </Button>

        </Stack>

     )


}
