                            checked={ques+props.qId === props.selectId}
                            onChange={ () => {
                                // formik.values["q"+props.qId] = true;
                                formik.values["sq"+props.qId]= ques;
                                setFuns[props.qId](ques+props.qId);
                                // switch (props.qId){
                                //     case 1:
                                //         console.log("case1")
                                //         setID1(ques+props.qId)
                                //         break
                                //     case 2:
                                //         console.log("case2")
                                //         setID2(ques+props.qId)
                                //         break
